import React from 'react';
import axios from 'axios';


class App extends React.Component{
  state = {
    isLoading: true,
    movies: []
  }


  componentDidMount(){   //render() 함수가 실행되고 나서 젤 먼저 실행되는 함수임.
    setTimeout(() =>{
      this.setState({      // setState 는 state의 값을 변경 시켜줄 때 사용
        isLoading: false
      })
    }, 6000);
  }

  render(){

    const {isLoading} = this.state;
    return (
      <div>
        {isLoading ? "Loading..." : "we are ready"}
      </div>
    )
  }


}

export default App;
